mapper.select:
select ME.REASON_FOR_CANCELLATION reasonForCancellation, ME.EVALUATION_TIME evaluationTime, ME.STATUS status, ME.LOANS_MONEY loansMoney, ME.SHAREHOLDING_RATIO shareholdingRatio, ME.PAWN pawn, O.ORGNAME orgname from (select * from SERVICE_COMPANY_INFO
) ME LEFT OUTER JOIN T_ORGANS O ON ME.COMPANY_ID = O.ID
order by ${orderByClause}
